import React, { useState } from "react";
import Board from "./Board";
import "./gameStyle.css";
import { chooseWinner } from "../../helper";
const GameTic = () => {
  const [board, setBoard] = useState(Array(9).fill(null));
  const [xIsNext, setXIsNext] = useState(true);
  const winner = chooseWinner(board);
  const handleClick = (index) => {
    const boardCopy = [...board]; //clone board
    if (winner || boardCopy[index]) return;
    boardCopy[index] = xIsNext ? "X" : "O";
    setBoard(boardCopy); //update board
    setXIsNext(!xIsNext); // click vô thành cái ngược lại.Vd nhấn X xong đến O; O xong đến X
  };
  const handleReplay = () => {
    setBoard(Array(9).fill(null));
  };
  return (
    <div>
      <Board cells={board} onClick={handleClick}></Board>
      {winner ? `The winner is ${xIsNext ? "O" : "X"}` : ""};
      <button className="replayBtn" onClick={handleReplay}>Replay</button>
    </div>
  );
};

export default GameTic;
