import logo from "./logo.svg";
import "./App.css";
import { ytbData } from "./YoutubeData";
//JSX: Javascript xml
//ES6
//Babel
/**
 *
 * element = <div id"root">Hello world</div>    => JSX khong phai HTMl
 * functionc createElement(elementType, props,.... children)
 * elementType: div, p, span
 * props: className, id, src, alt
 * ...children:
 * element2 = (<div>
 *  <span> Hello </span> <span> World </span>
 * </div>
 */

function Feature() {
  return (
    <div className="feature">
      <img src="" alt="" className="featureImg" />
      <h3 className="featureTitle">Title 1</h3>
      <p className="featureDescription">
        Lorem ipsum is a dummy text - Lorem ipsum is a dummy text

      </p>
    </div>
  );
}
//parent component
function App() {
  // const name = "Len Vo";
