  //   return `${firstname} ${lastname}`;
  // const number =[1,2,3,4,5];
  // const double = number.map(item => item * item);
  // console.log(double);
  return (
    <div className="ytb-list">
    {/* rendering list */}
      {ytbData.map((item, index) => {
        let newClassName = '';
        if(index === 1){
          newClassName = 'abcxyz'
        }
        return (
        <YoutubeItem
          key={item.id}
          image={item.image}
          author={item.author}
          avatar={item.avatar || item.image}
          title= {item.title}
          className= {index === 0 ? "abcxyz" : ""} 
        ></YoutubeItem>
      )})}
    </div>
  );
}
